This example shows how to update document properties in Word format and commit changes

// path to the MS Word file
string path = @"C:\\example.docx";

// initialize DocFormat
DocFormat docFormat = new DocFormat(path);

// update document properties
docFormat.DocumentProperties.Author = "Joe Doe";
docFormat.DocumentProperties.Company = "Aspose";

// and commit changes
docFormat.Save();

This example demonstrates how read known metadata properties of specific format directly.

string dir = @"c:\download files\";

// get all files inside 'Download Files' directory
string[] files = Directory.GetFiles(dir);

// recognize first file
FormatBase format = FormatFactory.RecognizeFormat(files[0]);

// try get EXIF artist
var exifArtist = format[MetadataKey.EXIF.Artist];
Console.WriteLine(exifArtist);

// try get dc:creator XMP value
var creator = format[MetadataKey.XMP.DublinCore.Creator];
Console.WriteLine(creator);

// try get xmp:creatorTool
var creatorTool = format[MetadataKey.XMP.BaseSchema.CreatorTool];
Console.WriteLine(creatorTool);

// try get IPTC Application Record keywords
var iptcKeywords = format[MetadataKey.IPTC.ApplicationRecord.Keywords];
Console.WriteLine(iptcKeywords);

This example demonstrates how to update XMP metadata in jpeg file.


// path to the jpg file
string path = @"C:\\example.jpg";
// initialize JpegFormat
JpegFormat jpegFormat = new JpegFormat(path);

// get XMP wrapper
XmpPacketWrapper xmpPacket = jpegFormat.GetXmpData();

// create XMP wrapper is not exist
if (xmpPacket == null)
{
xmpPacket = new XmpPacketWrapper();
}

// check if DublinCore schema is exist
if (!xmpPacket.ContainsPackage(Namespaces.DublinCore))
{
// if no - add DublinCore schema
xmpPacket.AddPackage(new DublinCorePackage());
}

// get DublinCore package
DublinCorePackage dublinCorePackage = (DublinCorePackage)xmpPacket.GetPackage(Namespaces.DublinCore);

// set author
dublinCorePackage.SetAuthor("Test author");

// set description
dublinCorePackage.SetDescription("Some description");

// update XMP package
jpegFormat.SetXmpData(xmpPacket);

// and commit changes
jpegFormat.Save(@"C:\\result.jpg");

