This example demonstrates how to get metadata.


// path to the dir with files
string dir = @"C:\\files";

// get array of files inside directory
string[] files = Directory.GetFiles(dir);

// go through files
foreach (string path in files)
{
// and get metadata array for each file
Metadata[] metadataArray = MetadataUtility.ExtractMetadata(path);

foreach (Metadata metadata in metadataArray)
{
switch (metadata.MetadataType)
{
case MetadataType.Doc:
// Cast metadata to DocMetadata
DocMetadata docProperties = (DocMetadata)metadata;

// get author
string author = docProperties.Author;

// get document comments
DocComment[] comments = docProperties.DocumentComments;
break;

case MetadataType.EXIF:
// Cast metadata to ExifMetadata
ExifMetadata exifMetadata = (ExifMetadata)metadata;
break;

case MetadataType.XMP:
// Cast metadata to ExifMetadata
XmpMetadata xmpMetadata = (XmpMetadata)metadata;

// get XMP wrapper
XmpPacketWrapper xmpWrapper = xmpMetadata.XmpPacket;

// get packages count
int packagesCount = xmpWrapper.PackagesCount;

break;
}
}
}

This example demonstrates how to read all XMP properties in specific file.

// path to the modified file
string path = "example.jpg";

// get XMP wrapper
XmpPacketWrapper xmpWrapper = MetadataUtility.ExtractXmpPackage(path);

if (xmpWrapper != null)
{
foreach (XmpPackage xmpPackage in xmpWrapper.Packages)
{
// show package xml namespace
Console.Write("Package xml namespace: {0}", xmpPackage.XmlNamespace);

foreach (string key in xmpPackage.Keys)
{
var value = xmpPackage[key];
if (value is XmpText)
{
string strValue = (value as XmpText).Value;
Console.WriteLine("Text key: {0}, value: {1}", key, strValue);
}
else if (value is XmpInteger)
{
int intValue = (int)(value as XmpInteger).Value;
Console.WriteLine("Integer key: {0}, value: {1}", key, intValue);
}
else if (value is XmpGuid)
{
Guid guidValue = (value as XmpGuid).Value;
Console.WriteLine("Guid key: {0}, value: {1}", key, guidValue);
}
else if (value is XmpDate)
{
DateTime dateValue = (value as XmpDate).Value;
Console.WriteLine("DateTime key: {0}, value: {1}", key, dateValue);
}                        
}
}
}

